using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.SceneManagement;public class Asteroid : MonoBehaviour{    [SerializeField]    private float _rotateSpeed;    private  Animator _animator;    private AudioSource _audioSource;    [SerializeField]    private AudioClip _asteroidDestroySound;      SpawnManager _spawnManager;    // Start is called before the first frame update    void Start()    {        _animator = GetComponent<Animator>();                if(_animator == null)         {            Debug.Log("animator exists");        }        _spawnManager = GameObject.FindWithTag("spawnManager").GetComponent<SpawnManager>();             if (_spawnManager == null)         {            Debug.Log("spawnManager exists");                }        _audioSource = GetComponent<AudioSource>();        if(_audioSource == null )        {            Debug.Log("asteroid audiosource non existang");        }        else         {            _audioSource.clip =_asteroidDestroySound;        }    }    // Update is called once per frame    void Update()    {        transform.Rotate(0f, 0f, _rotateSpeed);        //rotate on the z axis.     }    private void OnTriggerEnter2D(Collider2D other)    {        if (other.tag == "laser")        {                       _animator.SetTrigger("AsteroidExplosion");            _audioSource.Play();            _spawnManager.StartSpawning();            Destroy(GetComponent<Collider2D>());            Destroy(gameObject, 2.5f);                  }    }}