using System.Collections;using System.Collections.Generic;using UnityEngine;public class EnemyLaser : MonoBehaviour{    [SerializeField]    private float _enemylaserSpeed;      //get the player  and assign it to the damage    [SerializeField]     private Player  _player;      // Start is called before the first frame update    void Start()    {        _player = GameObject.FindWithTag("player").GetComponent<Player>();        if(_player != null)        {            Debug.Log("player exists");        }    }        // Update is called once per frame        void Update()    {        StartCoroutine ( RandomTimeLaser ());               }    private void EnemyLaserMovement()     {         transform.Translate(Vector3.down * _enemylaserSpeed  * Time.deltaTime);         //         if (transform.position.y > 8f)          {             if (transform.parent != null)            {                Destroy(transform.parent.gameObject);            }              Destroy(this.gameObject);         }     }    private IEnumerator RandomTimeLaser()     {        float _randTime = Random.Range(0.0f, 2.0f);       // Debug.Log("random time " + _randTime);        yield return new WaitForSeconds(_randTime);        EnemyLaserMovement();    }    private void OnTriggerEnter2D(Collider2D other)     {      //  Debug.Log("enemy laser has collided with : " + other.name);            if(other.tag == "player")         {            _player.Damage();        }    }}