using System.Collections;using System.Collections.Generic;using UnityEngine;using UnityEngine.UI;public class FillBar : MonoBehaviour{    public Slider  _slider;    public Text _text;    [SerializeField]    private float _timeToWait = 1.0f;    [SerializeField]    private int _currentValue = 1;    [SerializeField]    private bool _isActive  = false;     // Start is called before the first frame update    void Start()    {         }    // Update is called once per frame    void Update()    {    }    public void Slider()    {        if (_currentValue < 100)        {                    for (var i = 0; i < 100; i++)            {                            _slider.value = i;                _text.text = (_slider.value * 100).ToString();                Debug.Log("Slidervalue : " + _slider.value);                StartCoroutine(PauseForAWhile());            }                  }        IEnumerator PauseForAWhile()        {            yield return new WaitForSeconds(_timeToWait);            _currentValue = 0;            _slider.value = 0;        }    }}