using System.Collections;using System.Collections.Generic;using UnityEngine;public class PowlerUps : MonoBehaviour{    //handle to the player    Player _player;    FillBar _fillBar;    [SerializeField]// 0 is tripleshotPowerupS, 1 is SpeedBoast 2 Sheilds.    private int _powerUpId;    [SerializeField]    private float _speedDown = 3.0f;    private AudioSource _audioSource;    [SerializeField]    private AudioClip _powerUpCollectedSound;    // Start is called before the first frame update    void Start()    {        //turn off shields             _player = GameObject.FindWithTag("player").GetComponent<Player>();        if (_player == null)        {            Debug.Log("player ");        }        _fillBar = GameObject.FindWithTag("fillBar").GetComponent<FillBar>();        if(_fillBar != null)         {            Debug.Log("fillbar exists");        }        _audioSource = GetComponent<AudioSource>();        if(_audioSource == null)         {            Debug.Log("audioSourece on PowerUps problem ");        }        else         {            _audioSource.clip = _powerUpCollectedSound;        }    }    // Update is called once per frame    void Update()    {        transform.Translate(Vector3.down * Time.deltaTime * _speedDown);        if (transform.position.y < -4.5f)        {            Destroy(gameObject);        }    }    private void OnTriggerEnter2D(Collider2D other)    {       // Debug.Log(other.name);        if (other.tag == "player")        {            switch (_powerUpId)            {                case 0:                    _player.TripleShotActive();                    TripleShotTime();                    Debug.Log("TripleShot case 0");                    _audioSource.Play();                    break;                                case 1:                    Debug.Log("Speed case 1 ");                    // if collided with speed booast                      _player.SpeedIncrease();                    _audioSource.Play();                    // tell player that shields are enabled.                     break;                case 2 :                                    _player._isShieldsActive = true;                     _player.Shield();                                        _audioSource.Play();                    break;                case 3 :                    _player.Ammo();                    Debug.Log("Ammo Case 3 ");                    _audioSource.Play();                    break;                case 4 :                     _player.AddALife();                    Debug.Log("Health Case 4 ");                    _audioSource.Play();                    if(_player._lives < 3)                    {                        _fillBar.Slider();                    }                    break;                                                            default:                    Debug.Log("Default case");                    break;            }            Destroy(gameObject, .2f);        }        IEnumerator TripleShotTime()       {          yield return new WaitForSeconds(5f);            _player._isTripleShot = false;       }    }}