using System.Collections;using System.Collections.Generic;using UnityEngine;public class ShieldScript : MonoBehaviour{    private int _colorChange = 0;    Player _player;    Animator _animator;    private bool isOrange;    // Start is called before the first frame update    void Start()    {        _animator = GetComponent<Animator>();        if(_animator != null)        {            Debug.Log("animator exists");        }        _player = GameObject.FindWithTag("player").GetComponent<Player>();        if(_player != null)        {            Debug.Log("shieldscript     player exists");        }    }    // Update is called once per frame    void Update()    {    }    private void OnTriggerEnter2D(Collider2D other)     {         if(_player._isShieldsActive == true)         {            if (other.tag == "enemy" || other.tag == "enemyLaser")            {                _colorChange++;              // Debug.Log("Shield has collided with :   " + other.tag);               // Debug.Log("Shield has collided with :   " + _colorChange);            }        }        if (_colorChange == 1)        {            _animator.SetBool("isGreen", true);            _animator.SetBool("isOrange", false);            _animator.SetBool("isRed", false);        }       else if (_colorChange == 2)        {            _animator.SetBool("isOrange", true);            _animator.SetBool("isRed", false);            _animator.SetBool("isGreen", false);        }          else if (_colorChange == 3)            {            _animator.SetBool("isRed", true);            _animator.SetBool("isOrange", false);            _animator.SetBool("isGreen", false);        }        else if (_colorChange == 4 )             {            _animator.SetBool("isExit", true);            _player._isShieldsActive = false;            _player.spriteRenderer.enabled = false;            _player. isSmallShieldsActive = false;            Debug.Log("ColorChange ");            Debug.Log(  _colorChange = 0);        }    }}