using System.Collections;using System.Collections.Generic;using UnityEngine;public class SpawnManager : MonoBehaviour{    [SerializeField]    private GameObject _enemy;    [SerializeField]    private GameObject _enemyContainer;    [SerializeField]    private bool _isPlayerDead = false;    [SerializeField]    private GameObject[] _powerUps;    //testing    public GameObject[] differentEnemies;    public GameObject eitherEnemy;    [SerializeField]    private int _randEnemyInt;    public bool isEnemyOn = false;    // Start is called before the first frame update    void Start()    {           }    // Update is called once per frame    void Update()    {         WhichEnemy();    }    //testing     private IEnumerator SpawnEnemyRoutine()    {        yield return new WaitForSeconds(1.0f);        while (_isPlayerDead == false)        {            // get gameobject to instantiate.            Vector3 posToSpawn = new Vector3(Random.Range(-8f, 8f),6f,0f);            GameObject newEnemy = Instantiate(eitherEnemy, posToSpawn, Quaternion.identity);            newEnemy.transform.parent = _enemyContainer.transform;            yield return new WaitForSeconds(5.0f);        }    }    IEnumerator PowerUpSpawn()    {        yield return new WaitForSeconds(1.0f);        while (_isPlayerDead == false)        {            float _randPos = Random.Range(-6f, 6f);            int randPowerUp = Random.Range(0 ,5);            Instantiate(_powerUps[randPowerUp], new Vector3(_randPos, 8, 0), Quaternion.identity);            float _randTime = Random.Range(3.0f, 8.0f);            yield return new WaitForSeconds(_randTime);        }    }    public void StopSpawning()    {        _isPlayerDead = true;    }    public void StartSpawning()    {        // yield return new WaitForSeconds(3.0f)         StartCoroutine(SpawnEnemyRoutine());        StartCoroutine(PowerUpSpawn());    }    private void WhichEnemy()    {        _randEnemyInt = Random.Range(0, 2);        eitherEnemy = differentEnemies[_randEnemyInt];    }}